import React, {useState} from 'react';
import {useParams} from "react-router-dom";
import axios from "axios";

const PostBody = () => {

    const { postId } = useParams();

    const [data, setData] = useState([]);

    axios.get(`http://localhost:5077/api/ArticleApi/GetBody?bodyId=${postId}`)
        .then(response => {
            setData(response.data);
        })
        .catch(error => {
            console.error(error);
        });

    return (
        <>
            {data.map(item => (
                <div key={item.id} className="container card mt-xl-2 pb-2" style={{borderWidth: '1px', borderColor: 'black'}}>
                    <h1>{item.body}</h1>
                </div>
            ))}
        </>
    );
};

export default PostBody;
