import axios from "axios";
import Cookies from "js-cookie";
import {encryptedRole} from "../extensions/encryption";

class AuthService {
    static async login(loginRequest) {
        await axios.post("http://localhost:5098/Login", loginRequest)
            .then((response) => {
                Cookies.set("token", response.data.result.token, {expires: 1});
                Cookies.set("role", encryptedRole(response.data.result.user.role), {expires: 1});
            })
    };

    static async registration(registrationRequest){
       await axios.post("http://localhost:5098/Login",registrationRequest);
    }
}

export default AuthService;
