import React, {useEffect, useState} from 'react';
import {useNavigate, useParams} from "react-router-dom";
import axios from "axios";
import Header from "../../components/Home/Header";
import ScreenshotSlider from "../../components/Article/ScreenshotSlider";
import {Image} from "react-bootstrap";
import Like from "../../like.svg";
import Dislike from "../../dislike.svg";
import {getRole} from "../../extensions/encryption";
import {toast} from "react-toastify";

const PostBody = () => {

    const {postId} = useParams();

    const navigate = useNavigate();

    const [isLiked, setIsLiked] = useState(false);
    const [isDisliked, setIsDisliked] = useState(false);
    const [likeCount, setLikeCount] = useState(0);
    const [dislikeCount, setDislikeCount] = useState(0)

    const likeStyle = {
        color: isLiked ? 'red' : 'gray',
        fontWeight: isLiked ? 'bold' : 'normal'
    };

    const dislikeStyle = {
        color: isDisliked ? 'red' : 'gray',
        fontWeight: isDisliked ? 'bold' : 'normal'
    };


    const [data, setData] = useState({articleHeader: {}, articleBody: {}});

    const imageUrls = [data.articleBody.imageUrl1, data.articleBody.imageUrl2, data.articleBody.imageUrl3];

    const handleLikeClick = () => {
        if (getRole() !== undefined) {
            if (isLiked) {
                setIsLiked(false);
                setLikeCount(likeCount - 1);
            } else {
                setIsLiked(true);
                setLikeCount(likeCount + 1);
                if (isDisliked) {
                    setIsDisliked(false);
                    setDislikeCount(dislikeCount - 1);
                }
            }
        } else {
            notifyExp();
            navigate("/Login");
        }
    };
    const handleDislikeClick = () => {
        if (getRole() !== undefined) {
            if (isDisliked) {
                setIsDisliked(false);
                setDislikeCount(dislikeCount - 1);
            } else {
                setIsDisliked(true);
                setDislikeCount(dislikeCount + 1);
                if (isLiked) {
                    setIsLiked(false);
                    setLikeCount(likeCount - 1);
                }
            }
        } else {
            notifyExp();
            navigate("/Login");
        }
    };

    const notifyExp = () => toast.error('You are not authorized', {
        position: "top-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        theme: "colored",
    });

    useEffect(() => {
        axios.get(`http://localhost:5098/GetBody/${postId}`)
            .then(response => {
                setData(response.data);
                setLikeCount(data.articleHeader.likes)
            })
            .catch(error => {
                console.error(error);
            });
    }, [postId, data.articleHeader.likes]);

    return (
        <>
            <Header/>
            <div key={data.articleHeader.id} className="container mt-xl-2 pb-2"
                 style={{borderWidth: '1px', borderColor: 'black', backgroundColor: "white", borderRadius: "10px"}}>
                <div className={"row"}>
                    <div className={"col-xl-1 mt-xl-3 col-3 text-xl-center"} onClick={handleLikeClick}>
                        <h6 style={likeStyle}>{likeCount} <Image src={Like} style={{width: '35%'}} alt='Like'/></h6>
                    </div>
                    <div className={"col-xl-1 mt-xl-3 col-3 text-xl-start"} onClick={handleDislikeClick}>
                        <h6 style={dislikeStyle}>{dislikeCount} <Image src={Dislike} style={{width: '35%'}} alt='Dislike'/></h6>
                    </div>
                </div>
                <h2 className={'text-center fw-bold'}>{data.articleHeader.title}</h2>
                <h3 className={'text-center fw-bold'}>{data.articleHeader.header}</h3>
                <h4>{data.articleBody.body}</h4>
                <ScreenshotSlider imageUrls={imageUrls}/>
            </div>
        </>
    );
};

export default PostBody;
