import React, {useState} from 'react';
import axios from "axios";
import {Button} from "react-bootstrap";
import Cookies from 'js-cookie';
import {useNavigate} from "react-router-dom";
import {EncryptedRole} from "../extensions/encryption";

const LoginForm = () => {

    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post("http://localhost:5098/Login", loginRequest)
            .then((response) => {
                console.log(response.data);
                Cookies.set('token', response.data.result.token, {expires: 1});
                Cookies.set('role', EncryptedRole(response.data.result.user.role), {expires: 1})
                navigate("/");
            })
            .catch((error) => {
                console.error(error);
            });
    };

    const [loginRequest, loginRequestData] = useState({
        username: "",
        password: ""
    });

    const handleChange = (event) => {
        const {name, value} = event.target;
        loginRequestData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    };


    return (
        <>
            <div className={"container"}>
                <div className={"row"}>
                    <form onSubmit={handleSubmit}>
                        <div className={"col-xl-12"}>
                            <div className={"row"}>
                                <div className={"offset-xl-3 col-xl-6 mt-3"}>
                                    <input placeholder="Username" className={"form-control"} type="text" id="username"
                                           name="username" value={loginRequest.username} onChange={handleChange}/>
                                </div>
                                <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                    <input placeholder="Password" className={"form-control"} type="password" id="password"
                                           name="password" value={loginRequest.password} onChange={handleChange}/>
                                </div>
                                <div className={"offset-xl-8 col-xl-1 mt-2"}>
                                    <Button style={{color: "white"}} variant={"primary"} type="submit">Submit</Button>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
        </>
    );
};

export default LoginForm;
