import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import axios from "axios";
import Header from "../../components/Home/Header";
import ScreenshotSlider from "../../components/Article/ScreenshotSlider";
import {Image} from "react-bootstrap";
import Like from "../../like.svg";

const PostBody = () => {

    const {postId} = useParams();

    const [data, setData] = useState({ articleHeader: {}, articleBody: {} });

    useEffect(() => {
        axios.get(`http://localhost:5098/GetBody/${postId}`)
            .then(response => {
                setData(response.data);
            })
            .catch(error => {
                console.error(error);
            });
    }, [postId]);

    const imageUrls = [data.articleBody.imageUrl1, data.articleBody.imageUrl2, data.articleBody.imageUrl3];

    return (
        <>
            <Header/>
            <div key={data.articleHeader.id} className="container card mt-xl-2 pb-2"
                 style={{borderWidth: '1px', borderColor: 'black'}}>
                <h2 className={'text-center fw-bold'}>{data.articleHeader.title}</h2>
                <h3 className={'text-center fw-bold'}>{data.articleHeader.header}</h3>
                <h4>{data.articleBody.body}</h4>
                <ScreenshotSlider imageUrls={imageUrls}/>
                <div className={"col-xl-1 mt-xl-3 col-3 text-xl-center"}>
                <h6 style={{color: "gray"}}>{data.articleHeader.likes} <Image src={Like} style={{width: '35%'}} alt='Like'/></h6>
                </div>
            </div>
        </>
    );
};

export default PostBody;
