import React, {useEffect, useState} from 'react';
import {Button, Image} from "react-bootstrap";
import {Link} from "react-router-dom";
import ArticleService from "../../services/ArticleService";

const Card = () => {

    const [data, setData] = useState([]);

    const fetchData = async () => {
        try {
            setData(await ArticleService.getHeaders());
        } catch (error) {
            console.error('ArticleAPI stopped');
        }
    };

    useEffect(()  => {
        fetchData();
    }, []);

    return (
        <>
            {data.map(item => (
                <div key={item.id} className="container mt-xl-2 pb-2" style={{backgroundColor: 'white'}}>
                    <div className="row">
                        <div className="col-xl-1">
                            <h5>Author</h5>
                        </div>
                        <div className="col-xl-11 text-xl-start mt-xl-1">
                            <h6 style={{color: "gray"}}>Tuesday, 14 November</h6>
                        </div>
                        <div className="col-xl-9 col-12">
                            <h1 style={{color: 'black'}}>{item.title}</h1>
                        </div>
                        <div className="col-xl-3 col-12 text-xl-end mt-xl-3">

                        </div>
                        <div className="col-xl-6 col-12">
                            <Image src={item.imageUrl} className="rounded" width="100%" alt="Image"/>
                        </div>
                        <div className="col-xl-6 col-12 mt-xl-0 mt-2">
                            <h4>{item.header}</h4>
                        </div>
                        <div className="col-xl-12 text-end">
                            <Link to={`/post/${item.articleBodyId}`}>
                                <Button variant={"light"}>More details</Button>
                            </Link>
                        </div>
                    </div>
                </div>
            ))}
        </>
    );
};

export default Card;
