import React, {useState} from 'react';
import axios from "axios";
import {Button} from "react-bootstrap";

const NewArticleForm = () => {

    const [formData, setFormData] = useState({
        title: "",
        header: "",
        imageUrl: "",
        body: "",
        imageUrl1: "",
        imageUrl2: "",
        imageUrl3: "",
    });

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post("http://localhost:5098/CreateArticle", formData)
            .then((response) => {
                console.log(response.data);
                setRedirect(true);
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    };

    const handleChange = (event) => {
        const {name, value} = event.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    };

    if (redirect) {
        return <Redirect to="/новый_путь" />;
    }

    return (
        <div className={"container"}>
            <div className={"row"}>
                <form onSubmit={handleSubmit}>
                    <div className={"col-xl-12"}>
                        <div className={"row"}>
                            <div className={"offset-xl-3 col-xl-6 mt-3"}>
                                <input placeholder="Title" className={"form-control"} type="text" id="title"
                                       name="title" value={formData.title} onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                <input placeholder="Header" className={"form-control"} type="text" id="header"
                                       name="header" value={formData.header}
                                       onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                <input placeholder="ImageUrl" className={"form-control"} type="text" id="imageUrl"
                                       name="imageUrl" value={formData.imageUrl}
                                       onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                <textarea placeholder="Body" className={"form-control"} id="body" name="body"
                                          value={formData.body} onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                <input placeholder="ImageUrl1" className={"form-control"} type="text" id="imageUrl1"
                                       name="imageUrl1" value={formData.imageUrl1}
                                       onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                <input placeholder="ImageUrl2" className={"form-control"} type="text" id="imageUrl2"
                                       name="imageUrl2" value={formData.imageUrl2}
                                       onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-3 col-xl-6 mt-2"}>
                                <input placeholder="ImageUrl3" className={"form-control"} type="text" id="imageUrl3"
                                       name="imageUrl3" value={formData.imageUrl3}
                                       onChange={handleChange}/>
                            </div>
                            <div className={"offset-xl-8 col-xl-1 mt-2"}>
                                <Button style={{color: "white"}} variant={"info"} type="submit">Submit</Button>
                            </div>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default NewArticleForm;
