import React, {useEffect, useState} from 'react';
import {Button, Image} from "react-bootstrap";
import axios from "axios";
import {Link} from "react-router-dom";
import {getCard} from "../../services/articleService";

const Card = () => {

    const [data, setData] = useState([]);

    useEffect(() => {
        setData(getCard);
    }, []);

    return (
        <>
            {data.map(item => (
                <div key={item.id} className="container card mt-xl-2 pb-2" style={{borderWidth: '1px', borderColor: 'black'}}>
                    <div className="row">
                        <div className="col-xl-9 col-12">
                            <h1 style={{color: 'black'}}>{item.title}</h1>
                        </div>
                        <div className="col-xl-3 col-12 text-xl-end mt-xl-3">
                            <h4 style={{color: 'black'}}>Tuesday, 14 November</h4>
                        </div>
                        <div className="col-xl-6 col-12">
                            <Image src={item.imageUrl} className="rounded" width="100%" alt="Image"/>
                        </div>
                        <div className="col-xl-6 col-12 mt-xl-0 mt-2">
                            <h4>{item.header}</h4>
                        </div>
                        <div className="col-xl-12 text-end">
                            <Link to={`/post/${item.articleBodyId}`}>
                                <Button variant={"light"}>More details</Button>
                            </Link>
                        </div>
                    </div>
                </div>
            ))}
        </>
    );
};

export default Card;
