import axios from 'axios';
import config from '../config.json';
class ArticleService {}
ArticleService.getHeaders = async () => (await axios.get(config.gatewayUrl + "GetHeaders")).data;
ArticleService.getUserFeedbackLikeArticles = async () => (await axios.get(config.gatewayUrl + "GetUserFeedbackLikeArticles")).data;
ArticleService.getUserFeedbackDislikeArticles = async () => (await axios.get(config.gatewayUrl + "GetUserFeedbackDislikeArticles")).data;
ArticleService.getUserFeedbackById = async () => (await axios.get(config.gatewayUrl + "GetUserFeedbackById")).data;
ArticleService.getBody = async bodyId => (await axios.get(config.gatewayUrl + "GetBody/" + bodyId)).data;
ArticleService.getUserFeedback = async bodyId => (await axios.get(config.gatewayUrl + "GetUserFeedbackArticle/" + bodyId)).data;
ArticleService.addLike = async bodyId => await axios.post(config.gatewayUrl + "AddLike/" + bodyId);
ArticleService.sendNotificationAuthor = async userId => await axios.post("http://localhost:5252/PostMessage?userId=" + userId);
ArticleService.removeLike = async bodyId => await axios.post(config.gatewayUrl + "RemoveLike/" + bodyId);
ArticleService.addDislike = async bodyId => await axios.post(config.gatewayUrl + "AddDislike/" + bodyId);
ArticleService.removeDislike = async bodyId => await axios.post(config.gatewayUrl + "RemoveDislike/" + bodyId);
ArticleService.incrementLikeArticle = async bodyId => await axios.post(config.gatewayUrl + "IncrementLikeArticle/" + bodyId);
ArticleService.decrementLikeArticle = async bodyId => await axios.post(config.gatewayUrl + "DecrementLikeArticle/" + bodyId);
ArticleService.incrementDislikeArticle = async bodyId => await axios.post(config.gatewayUrl + "IncrementDislikeArticle/" + bodyId);
ArticleService.decrementDislikeArticle = async bodyId => await axios.post(config.gatewayUrl + "DecrementDislikeArticle/" + bodyId);
ArticleService.incrementArticleView = async bodyId => await axios.post(config.gatewayUrl + "IncrementArticleView/" + bodyId);
ArticleService.createArticle = async article => await axios.post(config.gatewayUrl + "CreateArticle", article);
export default ArticleService;
