import { AES } from 'crypto-js';
import Cookies from 'js-cookie';

const secretKey = 'SecretKey0_o'
export const encryptedRole = (role) => {
    return AES.encrypt(role, secretKey).toString();
}

export const getRole = () => {
    const decryptedRole = AES.decrypt(Cookies.get('role'), secretKey).toString();
    return hexToString(decryptedRole);
}

const hexToString = (hex) => {
    const charCodes = hex.match(/.{1,2}/g).map((pair) => parseInt(pair, 16));
    return String.fromCharCode(...charCodes);
};
