}

export const getRole = () => {
    console.log('Сейчас в теле getRole')
    const role = GetCookie();
    console.log(role);
    return AES.decrypt(GetCookie, secretKey).toString();
}

const GetCookie =  () => {
    console.log('Сейчас в теле getCookie')
    const [cookies] = useCookies(['role']);
    console.log(cookies.role)
    return cookies.role;
}
