import { AES } from 'crypto-js';

const secretKey = 'SecretKey0_o'
export const encryptedRole = (role) => {
    return AES.encrypt(role, secretKey).toString();
}

export const getRole = () => {
    const role = getCookieValue('role');
    const decryptedRole = AES.decrypt(role, secretKey).toString();
    const decodedRole = hexToString(decryptedRole);
    console.log(decodedRole);
    return decodedRole;
}

const getCookieValue = (name) => {
    const cookies = document.cookie.split(';');
    for (let i = 0; i < cookies.length; i++) {
        const cookie = cookies[i].trim();
        if (cookie.startsWith(name + '=')) {
            return cookie.substring(name.length + 1);
        }
    }
    return '';
};

const hexToString = (hex) => {
    let string = '';
    for (let i = 0; i < hex.length; i += 2) {
        string += String.fromCharCode(parseInt(hex.substr(i, 2), 16));
    }
    return string;
};
