import React, { useEffect, useState } from 'react';
import { HubConnectionBuilder } from '@microsoft/signalr';
import {useCookies} from "react-cookie";

const Notifications = () => {

    const [notification, setNotification] = useState([]);

    useEffect(() => {

        const connection = new HubConnectionBuilder()
            .withUrl('http://localhost:5252/notifications', {
                accessTokenFactory: () => 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJlbWFpbCI6ImQxczhoN2hAdmsuY29tIiwic3ViIjoiZTg1OWZhMWYtNTE2Yy00YzdlLThiNTYtODlmOTY3YWU2YjFlIiwibmFtZSI6ImFkbWluIiwicm9sZSI6IkFETUlOIiwibmJmIjoxNzAzNDA4NjU3LCJleHAiOjE3MDM0OTUwNTcsImlhdCI6MTcwMzQwODY1NywiaXNzIjoid2VidGVjaC1hdXRoLWFwaSIsImF1ZCI6IndlYnRlY2gtY2xpZW50In0.wjInImxJjkXJfLeTNbYkF8Kq5UiqUAjZnbhjVpKA3U4'
            })
            .build();

        connection.start()
            .then(() => {
                connection.invoke('GetString')
                    .then(result => {
                        console.log(result); // Выводит "Hello, world!" в консоль
                    })
                    .catch(error => {
                        console.error(error);
                    });
            })
            .catch(console.error);

        connection.on('ReceiveNotification', (message) => {
            setNotification(prevNotifications => [...prevNotifications, message]);
        });
    }, []);

    return () => {
        // Код для закрытия подключения
        if (connection) {
            connection.stop()
                .then(() => {
                    console.log('Подключение закрыто во время размонтирования');
                })
                .catch((err) => {
                    console.error('Ошибка при закрытии подключения во время размонтирования:', err);
                });
        }
    };
};

export default Notifications;
