import { AES } from 'crypto-js';
import {useCookies} from "react-cookie";

const secretKey = 'SecretKey0_o'
export const encryptedRole = (role) => {
    return AES.encrypt(role, secretKey).toString();
}

export const getRole = () => {
    return AES.decrypt(GetCookie, secretKey).toString();
}

const GetCookie =  () => {
    const [cookies] = useCookies(['role']);
    return cookies.role;
}
