import React, {useEffect, useState} from 'react';
import LogoutButton from "../../components/Auth/LogoutButton";
import {useNavigate} from "react-router-dom";
import {getRole} from "../../extensions/encryption";
import AccountHeader from "../../components/Home/AccountHeader";
import {Button} from "react-bootstrap";
import MyArticles from "../../components/Feedback/MyArticles";
import LikedArticles from "../../components/Feedback/LikedArticles";
import DislikedArticles from "../../components/Feedback/DislikedArticles";
const PersonalArea = () => {

    const [componentToShow, setComponentToShow] = useState(null);

    const handleButtonClick = (component) => {
        setComponentToShow(component);
    };

    const navigate = useNavigate();

    useEffect(() => {
        getRole() === undefined && navigate("/login");
    }, [navigate]);


    return (
        <>
            <AccountHeader/>
            <div className={"container"}>
                <div className={"row"}>
                    <div className={"col-xl-11"}>
                        <div className={"row offset-xl-1 mt-3"} style={{backgroundColor: "white", borderRadius: "10px"}}>
                            <div className={"col-xl-12 mt-3"}>
                                <div className={"row"}>
                                    <h1 className={"text-xl-center"}>Personal Area</h1>
                                    <div className={"col-xl-3"}>
                                        <div className={"offset-xl-1"}>
                                            <h5><Button onClick={() => handleButtonClick('MyArticles')}>My
                                                articles</Button></h5>
                                            <h5><Button onClick={() => handleButtonClick('LikedArticles')}>Liked
                                                articles</Button></h5>
                                            <h5><Button onClick={() => handleButtonClick('DislikedArticles')}>Disliked
                                                articles</Button></h5>
                                            <div><LogoutButton/></div>
                                        </div>
                                    </div>
                                    <div className={"col-xl-6"}>
                                        {componentToShow === 'MyArticles' && <MyArticles />}
                                        {componentToShow === 'LikedArticles' && <LikedArticles />}
                                        {componentToShow === 'DislikedArticles' && <DislikedArticles />}
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default PersonalArea;
