import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import axios from "axios";
import Header from "../../components/Home/Header";
import ScreenshotSlider from "../../components/Article/ScreenshotSlider";

const PostBody = () => {

    const {postId} = useParams();

    const [data, setData] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:5098/GetBody/${postId}`)
            .then(response => {
                setData(response.data);
            })
            .catch(error => {
                console.error(error);
            });

        axios.post(`http://localhost:5098/IncrementArticleView/${postId}`);

    }, []);

    const imageUrls = [data.imageUrl1,data.imageUrl2,data.imageUrl3];

    return (
        <>
            <Header/>
            <div key={data.id} className="container card mt-xl-2 pb-2"
                 style={{borderWidth: '1px', borderColor: 'black'}}>
                <h4>{data.body}</h4>
                <ScreenshotSlider imageUrls={imageUrls}/>
            </div>
        </>
    );
};

export default PostBody;
